import React from 'react';
import { StyleSheet, Text, View, Image, TextInput, TouchableOpacity, FlatList} from 'react-native';

export default function Stories() {

  const stories = [
    {
    id: 1,
    nome: Perna,
    src: require('../../assets/images/stories/pernalonga.png')
    },
    {
      id: 2,
      nome: 'Taz',
      src: require('../../assets/images/stories/taz.jpg')
    },
    {
      id: 3,
      nome: 'Frajola',
      src: require('../../assets/images/stories/frajola.jpg')
    },
    {
      id: 4,
      nome: 'Patolino',
      src: require('../../assets/images/stories/patolino.jpg')
    },
    {
      id: 5,
      nome: 'Felicia',
      src: require('../../assets/images/stories/felicia.png')
    },
    {
      id: 6,
      nome: 'Piu-Piu',
      src: require('../../assets/images/stories/piupiu.jpg')
    },
  ];

function renderItem({item}){
  return <View style={styles.story}>
      <Image style={styles.imgStory} source={item.src}></Image>
      <Text>{item.nome}</Text>
    </View>
}

    return (
      <View style={styles.stories}>
        <FlatList
          data={stories}
          renderItem={renderItem}
          keyExtractor={item => item.id}
          horizontal
          showsHorizontalScrollIndicator={false}
        />
      </View>
    )
}

const styles = StyleSheet.create({
    stories: {
        flexDirection: 'row',
        height: 90,
        backgroundColor: '#fff'
      },
      story: {
        height: 90,
        width: 90,
        backgroundColor: '#fff',
        alignItems: 'center'
      },
      imgStory: {
        borderRadius: 35,
        width: 70,
        height: 70
      },
})
