import React from 'react'
import { View, Text, StyleSheet, Image, FlatList } from 'react-native';
import {FontAwesome5} from '@expo/vector-icons';

export default function Feed() {

  const feed = [
    {
    id: 1,
    nome: 'Piu-Piu',
    perfilImg: require('../../assets/images/stories/piupiu.jpg'),
    img: require('../../assets/images/stories/tinytoons.jpg'),
    aspectRatio: 1.777
    },
    {
      id: 2,
      nome: 'Perna',
      perfilImg: require('../../assets/images/stories/pernalonga.png'),
      img: require('../../assets/images/stories/tinytoons3.jpg'),
      aspectRatio: 1.595
    },
    {
      id: 3,
      nome: 'Taz',
      perfilImg: require('../../assets/images/stories/taz.jpg'),
      img: require('../../assets/images/stories/tinytoons2.jpg'),
      aspectRatio: 0.712
    },
  ];

function renderItem({item}){
  return <View style={styles.post}>
  <View style={styles.postHeader}>
    <View style={styles.phEsquerda}>
        <Image style={styles.phImage} source={item.perfilImg}></Image>
        <Text>{item.nome}</Text>
    </View>
    <FontAwesome5
    name="ellipsis-h" size={16} color="black"
    />
  </View>
  <Image style={styles.postImg} aspectRatio={item.aspectRatio} source={item.img}/>
  <View style={styles.footer}>
  <FontAwesome5 style={styles.foosterIcon} name="heart" size={28} color="black"/>
  <FontAwesome5 style={styles.foosterIcon} name="comment" size={28} color="black"/>
  </View>
</View>
}

    return (
        <View style={styles.feed}>
        <FlatList
          data={feed}
          renderItem={renderItem}
          keyExtractor={item => item.id}
          showsVerticalScrollIndicator={false}
        />
      </View>
    )
}
const styles = StyleSheet.create({
    feed:{
        flex: 1,
      },
      post: {
    
      },
      postHeader:{
        height: 60,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
        margin: 5
      },
      phImage:{
        height: 50,
        width: 50,
        borderRadius: 25,
        marginRight: 5
      },
      phEsquerda:{
          flexDirection: 'row',
          alignItems: 'center',
      },
      postImg: {
          width: '100%',
          height: undefined,

      },
      footer: {
          height: 60,
          flexDirection: 'row',
          alignItems: 'center',
          padding: 5,
      },
      foosterIcon: {
          margin: 5,
      }
})
